package com.gankki.demo.oldthread.thread.homework;

public class Cove {
	private String name;
	private int distant;

	public Cove(String name, int distant) {
		super();
		this.name = name;
		this.distant = distant;
	}

	public Cove() {
		super();
		// TODO Auto-generated constructor stub
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

	public int getDistant() {
		return distant;
	}

	public void setDistant(int distant) {
		this.distant = distant;
	}

}
